import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Line, Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, BarElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, BarElement, Title, Tooltip, Legend);

const API_URL = 'http://127.0.0.1:5000';

const categories = [
  { value: 'C1', label: 'M01AB' },
  { value: 'C2', label: 'M01AE' },
  { value: 'C3', label: 'N02BA' },
  { value: 'C4', label: 'N02BE' },
  { value: 'C5', label: 'N05B' },
  { value: 'C6', label: 'N05C' },
  { value: 'C7', label: 'R03' },
  { value: 'C8', label: 'R06' },
];

function App() {
  const [activeTab, setActiveTab] = useState('forecast');
  const [category, setCategory] = useState('C1');
  const [model, setModel] = useState('sarimax');
  const [weeks, setWeeks] = useState(1);
  const [date, setDate] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [chartData, setChartData] = useState(null);

  // Evaluation states
  const [evaluationResults, setEvaluationResults] = useState(null);
  const [ensembleResults, setEnsembleResults] = useState(null);
  const [optimizationResults, setOptimizationResults] = useState(null);
  const [ensembleMethod, setEnsembleMethod] = useState('weighted_average');
  const [ensemblePrediction, setEnsemblePrediction] = useState(null);

  const tabs = [
    { id: 'forecast', label: 'Forecast', icon: 'ðŸ“Š' },
    { id: 'evaluation', label: 'Model Evaluation', icon: 'ðŸ“ˆ' },
    { id: 'ensemble', label: 'Ensemble Methods', icon: 'ðŸ”„' },
    { id: 'optimization', label: 'Hyperparameter Optimization', icon: 'âš™ï¸' }
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setResult(null);
    setChartData(null);

    const formData = new FormData();
    formData.append('category', category);
    formData.append('model_type', model);
    formData.append('week_offset', weeks);
    formData.append('date', date);

    try {
      const response = await axios.post(`${API_URL}/forecast`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      const data = response.data;
      setResult(data);

      // Create chart data from the plot URL
      if (data.plot_url) {
        setChartData({
          labels: ['Historical', 'Forecast'],
          datasets: [
            {
              label: 'Sales Data',
              data: [null, data.forecast_value],
              borderColor: 'rgb(75, 192, 192)',
              backgroundColor: 'rgba(75, 192, 192, 0.2)',
              tension: 0.1,
            },
          ],
        });
      }
    } catch (error) {
      setResult({ error: 'Failed to get prediction' });
    }
    setLoading(false);
  };

  const runEvaluation = async () => {
    setLoading(true);
    try {
      await axios.post(`${API_URL}/evaluate_models`);
      // Get results
      const response = await axios.get(`${API_URL}/get_evaluation_results`);
      setEvaluationResults(response.data);
    } catch (error) {
      console.error('Evaluation failed:', error);
    }
    setLoading(false);
  };

  const runEnsembleEvaluation = async () => {
    setLoading(true);
    try {
      await axios.post(`${API_URL}/evaluate_ensembles`);
      const response = await axios.get(`${API_URL}/get_ensemble_results`);
      setEnsembleResults(response.data);
    } catch (error) {
      console.error('Ensemble evaluation failed:', error);
    }
    setLoading(false);
  };

  const runEnsemblePrediction = async () => {
    setLoading(true);
    try {
      const formData = new FormData();
      formData.append('category', category);
      formData.append('method', ensembleMethod);
      formData.append('n_steps', 10);

      const response = await axios.post(`${API_URL}/forecast_ensemble`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setEnsemblePrediction(response.data);
    } catch (error) {
      console.error('Ensemble prediction failed:', error);
    }
    setLoading(false);
  };

  const runOptimization = async () => {
    setLoading(true);
    try {
      const formData = new FormData();
      formData.append('n_trials', 30);

      await axios.post(`${API_URL}/optimize_hyperparameters`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      const response = await axios.get(`${API_URL}/get_optimization_results`);
      setOptimizationResults(response.data);
    } catch (error) {
      console.error('Optimization failed:', error);
    }
    setLoading(false);
  };

  const renderEvaluationCharts = () => {
    if (!evaluationResults || !evaluationResults.summary) return null;

    const summary = evaluationResults.summary;
    const models = Object.keys(summary).filter(key => key !== 'ranking');

    const maeData = {
      labels: models,
      datasets: [{
        label: 'Mean Absolute Error',
        data: models.map(model => summary[model].final_mae),
        backgroundColor: 'rgba(255, 99, 132, 0.6)',
      }]
    };

    const timeData = {
      labels: models,
      datasets: [{
        label: 'Inference Time (seconds)',
        data: models.map(model => summary[model].final_time),
        backgroundColor: 'rgba(54, 162, 235, 0.6)',
      }]
    };

    return (
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-6">
        <div className="bg-white p-4 rounded-lg shadow">
          <h3 className="text-lg font-semibold mb-4">Model Performance (MAE)</h3>
          <Bar data={maeData} />
        </div>
        <div className="bg-white p-4 rounded-lg shadow">
          <h3 className="text-lg font-semibold mb-4">Inference Time</h3>
          <Bar data={timeData} />
        </div>
      </div>
    );
  };

  const renderEnsembleCharts = () => {
    if (!ensembleResults || !ensembleResults.results) return null;

    const methods = ['weighted_average', 'performance_weighted', 'stacking'];
    const categories = Object.keys(ensembleResults.results);

    const maeData = {
      labels: categories,
      datasets: methods.map((method, index) => ({
        label: method.replace('_', ' ').toUpperCase(),
        data: categories.map(cat => ensembleResults.results[cat][method]?.MAE || 0),
        backgroundColor: `rgba(${index * 80}, ${100 + index * 50}, ${200 - index * 50}, 0.6)`,
      }))
    };

    return (
      <div className="mt-6">
        <div className="bg-white p-4 rounded-lg shadow">
          <h3 className="text-lg font-semibold mb-4">Ensemble Methods Comparison (MAE)</h3>
          <Bar data={maeData} />
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
      <div className="container mx-auto px-4 py-8">
        <h1 className="text-4xl font-bold mb-8 text-indigo-700 text-center font-sans">
          Drug Sales Prediction Dashboard
        </h1>

        {/* Tab Navigation */}
        <div className="flex flex-wrap justify-center mb-8">
          {tabs.map((tab) => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`px-6 py-3 mx-2 mb-2 rounded-lg font-semibold transition duration-300 ${
                activeTab === tab.id
                  ? 'bg-indigo-600 text-white shadow-lg'
                  : 'bg-white text-indigo-600 hover:bg-indigo-100'
              }`}
            >
              {tab.icon} {tab.label}
            </button>
          ))}
        </div>

        {/* Forecast Tab */}
        {activeTab === 'forecast' && (
          <div className="bg-white rounded-2xl shadow-2xl p-8 max-w-4xl mx-auto">
            <form className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8" onSubmit={handleSubmit}>
              <div>
                <label className="block text-lg font-semibold mb-2 text-gray-700">Drug Category</label>
                <select
                  className="w-full border-2 border-gray-300 rounded-lg p-3 focus:border-indigo-500 focus:outline-none transition"
                  value={category}
                  onChange={(e) => setCategory(e.target.value)}
                >
                  {categories.map((cat) => (
                    <option key={cat.value} value={cat.value}>{cat.label}</option>
                  ))}
                </select>
              </div>
              <div>
                <label className="block text-lg font-semibold mb-2 text-gray-700">Prediction Model</label>
                <select
                  className="w-full border-2 border-gray-300 rounded-lg p-3 focus:border-indigo-500 focus:outline-none transition"
                  value={model}
                  onChange={(e) => setModel(e.target.value)}
                >
                  <option value="sarimax">SARIMAX</option>
                  <option value="xgboost">XGBoost</option>
                  <option value="transformer">Transformer</option>
                  <option value="gru">GRU</option>
                  <option value="lstm">LSTM</option>
                  <option value="lightgbm">LightGBM</option>
                  <option value="prophet">Facebook Prophet</option>
                </select>
              </div>
              <div>
                <label className="block text-lg font-semibold mb-2 text-gray-700">Weeks Ahead</label>
                <input
                  type="number"
                  min="1"
                  max="52"
                  className="w-full border-2 border-gray-300 rounded-lg p-3 focus:border-indigo-500 focus:outline-none transition"
                  value={weeks}
                  onChange={(e) => setWeeks(e.target.value)}
                  required
                />
              </div>
              <div>
                <label className="block text-lg font-semibold mb-2 text-gray-700">Starting Date</label>
                <input
                  type="date"
                  className="w-full border-2 border-gray-300 rounded-lg p-3 focus:border-indigo-500 focus:outline-none transition"
                  value={date}
                  onChange={(e) => setDate(e.target.value)}
                  required
                />
              </div>
              <div className="md:col-span-2">
                <button
                  type="submit"
                  className="w-full bg-indigo-600 text-white font-bold py-4 rounded-lg hover:bg-indigo-700 transition duration-300 shadow-lg"
                  disabled={loading}
                >
                  {loading ? 'Predicting...' : 'Predict Sales'}
                </button>
              </div>
            </form>

            {result && (
              <div className="bg-gray-50 rounded-lg p-6">
                {result.error ? (
                  <p className="text-red-600 text-center text-xl">{result.error}</p>
                ) : (
                  <div className="text-center">
                    <h2 className="text-2xl font-bold mb-4 text-green-600">Prediction Result</h2>
                    <p className="text-lg mb-2">Forecast Value: <span className="font-semibold text-indigo-600">{result.forecast_value?.toFixed(2)}</span></p>
                    <p className="text-lg mb-4">Closest Date: <span className="font-semibold">{result.closest_prediction_date}</span></p>
                    {result.plot_url && (
                      <div className="mt-6">
                        <img src={`${API_URL}${result.plot_url}`} alt="Forecast Plot" className="mx-auto rounded-lg shadow-lg" />
                      </div>
                    )}
                  </div>
                )}
              </div>
            )}
          </div>
        )}

        {/* Evaluation Tab */}
        {activeTab === 'evaluation' && (
          <div className="bg-white rounded-2xl shadow-2xl p-8 max-w-6xl mx-auto">
            <div className="text-center mb-8">
              <h2 className="text-3xl font-bold text-indigo-700 mb-4">Model Performance Evaluation</h2>
              <button
                onClick={runEvaluation}
                className="bg-green-600 text-white font-bold py-3 px-6 rounded-lg hover:bg-green-700 transition duration-300 shadow-lg"
                disabled={loading}
              >
                {loading ? 'Running Evaluation...' : 'Run Model Evaluation'}
              </button>
            </div>

            {evaluationResults && renderEvaluationCharts()}

            {evaluationResults && evaluationResults.summary && (
              <div className="mt-8">
                <h3 className="text-xl font-semibold mb-4">Performance Summary</h3>
                <div className="overflow-x-auto">
                  <table className="w-full table-auto border-collapse border border-gray-300">
                    <thead>
                      <tr className="bg-gray-100">
                        <th className="border border-gray-300 px-4 py-2">Model</th>
                        <th className="border border-gray-300 px-4 py-2">MAE</th>
                        <th className="border border-gray-300 px-4 py-2">RMSE</th>
                        <th className="border border-gray-300 px-4 py-2">MAPE (%)</th>
                        <th className="border border-gray-300 px-4 py-2">Time (s)</th>
                      </tr>
                    </thead>
                    <tbody>
                      {Object.keys(evaluationResults.summary).filter(key => key !== 'ranking').map(model => (
                        <tr key={model}>
                          <td className="border border-gray-300 px-4 py-2 font-semibold">{model.toUpperCase()}</td>
                          <td className="border border-gray-300 px-4 py-2">{evaluationResults.summary[model].final_mae?.toFixed(4) || 'N/A'}</td>
                          <td className="border border-gray-300 px-4 py-2">{evaluationResults.summary[model].final_rmse?.toFixed(4) || 'N/A'}</td>
                          <td className="border border-gray-300 px-4 py-2">{evaluationResults.summary[model].final_mape?.toFixed(4) || 'N/A'}</td>
                          <td className="border border-gray-300 px-4 py-2">{evaluationResults.summary[model].final_time?.toFixed(4) || 'N/A'}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            )}
          </div>
        )}

        {/* Ensemble Tab */}
        {activeTab === 'ensemble' && (
          <div className="bg-white rounded-2xl shadow-2xl p-8 max-w-6xl mx-auto">
            <div className="text-center mb-8">
              <h2 className="text-3xl font-bold text-indigo-700 mb-4">Ensemble Methods</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
                <div>
                  <label className="block text-lg font-semibold mb-2 text-gray-700">Drug Category</label>
                  <select
                    className="w-full border-2 border-gray-300 rounded-lg p-3 focus:border-indigo-500 focus:outline-none transition"
                    value={category}
                    onChange={(e) => setCategory(e.target.value)}
                  >
                    {categories.map((cat) => (
                      <option key={cat.value} value={cat.value}>{cat.label}</option>
                    ))}
                  </select>
                </div>
                <div>
                  <label className="block text-lg font-semibold mb-2 text-gray-700">Ensemble Method</label>
                  <select
                    className="w-full border-2 border-gray-300 rounded-lg p-3 focus:border-indigo-500 focus:outline-none transition"
                    value={ensembleMethod}
                    onChange={(e) => setEnsembleMethod(e.target.value)}
                  >
                    <option value="weighted_average">Weighted Average</option>
                    <option value="performance_weighted">Performance Weighted</option>
                    <option value="stacking">Stacking</option>
                  </select>
                </div>
              </div>
              <div className="flex justify-center space-x-4">
                <button
                  onClick={runEnsembleEvaluation}
                  className="bg-blue-600 text-white font-bold py-3 px-6 rounded-lg hover:bg-blue-700 transition duration-300 shadow-lg"
                  disabled={loading}
                >
                  {loading ? 'Evaluating...' : 'Evaluate Ensembles'}
                </button>
                <button
                  onClick={runEnsemblePrediction}
                  className="bg-green-600 text-white font-bold py-3 px-6 rounded-lg hover:bg-green-700 transition duration-300 shadow-lg"
                  disabled={loading}
                >
                  {loading ? 'Predicting...' : 'Generate Ensemble Prediction'}
                </button>
              </div>
            </div>

            {ensembleResults && renderEnsembleCharts()}

            {ensemblePrediction && (
              <div className="mt-8 bg-gray-50 rounded-lg p-6">
                <h3 className="text-2xl font-bold mb-4 text-green-600">Ensemble Prediction Result</h3>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
                  <div className="text-center">
                    <p className="text-lg font-semibold">MAE</p>
                    <p className="text-xl text-indigo-600">{ensemblePrediction.metrics?.MAE?.toFixed(4)}</p>
                  </div>
                  <div className="text-center">
                    <p className="text-lg font-semibold">RMSE</p>
                    <p className="text-xl text-indigo-600">{ensemblePrediction.metrics?.RMSE?.toFixed(4)}</p>
                  </div>
                  <div className="text-center">
                    <p className="text-lg font-semibold">MAPE (%)</p>
                    <p className="text-xl text-indigo-600">{ensemblePrediction.metrics?.MAPE?.toFixed(4)}</p>
                  </div>
                </div>
                {ensemblePrediction.plot_url && (
                  <div className="mt-6">
                    <img src={`${API_URL}${ensemblePrediction.plot_url}`} alt="Ensemble Plot" className="mx-auto rounded-lg shadow-lg" />
                  </div>
                )}
              </div>
            )}
          </div>
        )}

        {/* Optimization Tab */}
        {activeTab === 'optimization' && (
          <div className="bg-white rounded-2xl shadow-2xl p-8 max-w-4xl mx-auto">
            <div className="text-center mb-8">
              <h2 className="text-3xl font-bold text-indigo-700 mb-4">Hyperparameter Optimization</h2>
              <button
                onClick={runOptimization}
                className="bg-purple-600 text-white font-bold py-3 px-6 rounded-lg hover:bg-purple-700 transition duration-300 shadow-lg"
                disabled={loading}
              >
                {loading ? 'Optimizing...' : 'Run Hyperparameter Optimization'}
              </button>
            </div>

            {optimizationResults && (
              <div className="mt-8">
                <h3 className="text-xl font-semibold mb-4">Optimization Results</h3>
                <div className="space-y-6">
                  {Object.keys(optimizationResults.results || {}).map(model => (
                    <div key={model} className="bg-gray-50 rounded-lg p-6">
                      <h4 className="text-lg font-bold mb-3">{model.toUpperCase()}</h4>
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <div>
                          <p className="font-semibold">Best MAE: <span className="text-green-600">{optimizationResults.results[model].best_value?.toFixed(4)}</span></p>
                          <p className="font-semibold">Trials: {optimizationResults.results[model].n_trials}</p>
                        </div>
                        <div>
                          <p className="font-semibold mb-2">Best Parameters:</p>
                          <div className="text-sm bg-white p-2 rounded border max-h-32 overflow-y-auto">
                            {Object.entries(optimizationResults.params[model] || {}).map(([key, value]) => (
                              <div key={key}>{key}: {typeof value === 'number' ? value.toFixed(4) : value}</div>
                            ))}
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
