import React, { useState } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const API_URL = 'http://127.0.0.1:5000/forecast';

const categories = [
  { value: 'C1', label: 'M01AB' },
  { value: 'C2', label: 'M01AE' },
  { value: 'C3', label: 'N02BA' },
  { value: 'C4', label: 'N02BE' },
  { value: 'C5', label: 'N05B' },
  { value: 'C6', label: 'N05C' },
  { value: 'C7', label: 'R03' },
  { value: 'C8', label: 'R06' },
];

function App() {
  const [category, setCategory] = useState('C1');
  const [model, setModel] = useState('sarimax');
  const [weeks, setWeeks] = useState(1);
  const [date, setDate] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [chartData, setChartData] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setResult(null);
    setChartData(null);

    const formData = new FormData();
    formData.append('category', category);
    formData.append('model_type', model);
    formData.append('week_offset', weeks);
    formData.append('date', date);

    try {
      const response = await axios.post(API_URL, formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      const data = response.data;
      setResult(data);

      // Assuming the API returns chart data or we can generate it
      // For now, placeholder chart
      setChartData({
        labels: ['Week 1', 'Week 2', 'Week 3'],
        datasets: [
          {
            label: 'Predicted Sales',
            data: [data.forecast_value, data.forecast_value * 1.1, data.forecast_value * 1.2],
            borderColor: 'rgb(75, 192, 192)',
            tension: 0.1,
          },
        ],
      });
    } catch (error) {
      setResult({ error: 'Failed to get prediction' });
    }
    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex flex-col items-center justify-center p-4">
      <div className="bg-white rounded-2xl shadow-2xl p-8 max-w-4xl w-full">
        <h1 className="text-4xl font-bold mb-8 text-indigo-700 text-center font-sans">Drug Sales Prediction Dashboard</h1>
        <form className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8" onSubmit={handleSubmit}>
          <div>
            <label className="block text-lg font-semibold mb-2 text-gray-700">Drug Category</label>
            <select
              className="w-full border-2 border-gray-300 rounded-lg p-3 focus:border-indigo-500 focus:outline-none transition"
              value={category}
              onChange={(e) => setCategory(e.target.value)}
            >
              {categories.map((cat) => (
                <option key={cat.value} value={cat.value}>{cat.label}</option>
              ))}
            </select>
          </div>
          <div>
            <label className="block text-lg font-semibold mb-2 text-gray-700">Prediction Model</label>
            <select
              className="w-full border-2 border-gray-300 rounded-lg p-3 focus:border-indigo-500 focus:outline-none transition"
              value={model}
              onChange={(e) => setModel(e.target.value)}
            >
              <option value="sarimax">SARIMAX</option>
              <option value="xgboost">XGBoost</option>
              <option value="transformer">Transformer</option>
              <option value="gru">GRU</option>
            </select>
          </div>
          <div>
            <label className="block text-lg font-semibold mb-2 text-gray-700">Weeks Ahead</label>
            <input
              type="number"
              min="1"
              max="52"
              className="w-full border-2 border-gray-300 rounded-lg p-3 focus:border-indigo-500 focus:outline-none transition"
              value={weeks}
              onChange={(e) => setWeeks(e.target.value)}
              required
            />
          </div>
          <div>
            <label className="block text-lg font-semibold mb-2 text-gray-700">Starting Date</label>
            <input
              type="date"
              className="w-full border-2 border-gray-300 rounded-lg p-3 focus:border-indigo-500 focus:outline-none transition"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              required
            />
          </div>
          <div className="md:col-span-2">
            <button
              type="submit"
              className="w-full bg-indigo-600 text-white font-bold py-4 rounded-lg hover:bg-indigo-700 transition duration-300 shadow-lg"
              disabled={loading}
            >
              {loading ? 'Predicting...' : 'Predict Sales'}
            </button>
          </div>
        </form>
        {result && (
          <div className="bg-gray-50 rounded-lg p-6">
            {result.error ? (
              <p className="text-red-600 text-center text-xl">{result.error}</p>
            ) : (
              <div className="text-center">
                <h2 className="text-2xl font-bold mb-4 text-green-600">Prediction Result</h2>
                <p className="text-lg mb-2">Forecast Value: <span className="font-semibold text-indigo-600">{result.forecast_value?.toFixed(2)}</span></p>
                <p className="text-lg mb-4">Closest Date: <span className="font-semibold">{result.closest_prediction_date}</span></p>
                {chartData && (
                  <div className="mt-6">
                    <Line data={chartData} />
                  </div>
                )}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
